#!/bin/bash
emcc debug.c util.c parser.c bytecode.c run.c interaction.c renderer.c debugger.c -o debugger.js -O3 -lGLESv2 -lEGL -s USE_WEBGL2=1 -s ALLOW_MEMORY_GROWTH=1 -s "EXTRA_EXPORTED_RUNTIME_METHODS=['ccall', 'cwrap', 'writeAsciiToMemory', 'writeArrayToMemory']" --preload-file assets -Wno-incompatible-pointer-types